import { Component, OnInit } from '@angular/core';
import { Citas } from 'src/app/models';
import { FirestoreService } from 'src/app/services/firestore.service';
import pdfMake from "pdfmake/build/pdfmake";
import pdfFonts from "pdfmake/build/vfs_fonts";

import { DatePipe } from '@angular/common';
import { AppRoutingModule } from 'src/app/app-routing.module';


pdfMake.vfs = pdfFonts.pdfMake.vfs;

@Component({
  selector: 'app-reportecitasgeneral',
  templateUrl: './reportecitasgeneral.component.html',
  styleUrls: ['./reportecitasgeneral.component.scss'],
})
export class ReportecitasgeneralComponent implements OnInit {

  constructor(
    public firestoreservice: FirestoreService,
    private appaoutingaodule:AppRoutingModule,
  ) { }


  data: any[] = [['Codigo', 'Nombre de la mascota', 'Fecha', 'Motivo', 'Diagnostico', 'Estado de la cita'],];
  info: any;

  desdeescogido = false;
  desbloquearboton = true;
  mostrarreporte = false;
  totalcitas = 0;
  minhasta: any;
  pdfObj: any;
  today: any;
  desde: string;
  hasta: string;
  Citas: Citas[] = [];
  ngOnInit() {
    this.getAcutualDate();
  }



  getAcutualDate() {
    const date = new Date();
    this.today = date.getFullYear() + '-' + ('0' + (date.getMonth() + 1)).slice(-2) + '-' + ('0' + date.getDate()).slice(-2);
  }

  fechaescogida() {
    this.desdeescogido = true;
  }

  hastaescogido() {
    this.desbloquearboton = false;
  }

  generarpdf() {
    this.data=[['Codigo', 'Nombre de la mascota', 'Fecha', 'Motivo', 'Diagnostico', 'Estado de la cita'],];
    const path = 'Citas';

    try {
      this.firestoreservice.getCollectioncitasreporte<Citas>(path, 'fecha_cita', '>=', this.desde, 'fecha_cita', '<=', this.hasta).subscribe(res => {
        this.Citas = res;
        for (var i = 0; i <= this.Citas.length - 1; i++) {
          this.info = [this.Citas[i]['id_cita'], this.Citas[i]['namepet'],this.appaoutingaodule.cambiarfechageneral(this.Citas[i]['fecha_cita']) , this.Citas[i]['motivo_cita'], this.Citas[i]['diagnostico'], this.Citas[i]['estadodelacita']]

          this.data[i + 1] = this.info
          this.info = ''
          //this.data[i+1][1] = this.Citas[0]['diagnostico']
        };
      });

      this.mostrarreporte = true;
    } catch (error) {
      alert(error);
      this.desbloquearboton = true;
    }
  }


  //creacion del PDF
  pdf() {
    const docDef = {
      pageSize: 'A4',
      pageOrientation: 'portait',
      pageMargins: [20, 10, 40, 60],
      content: [
        {
          image:'data:image/jpeg;base64,'
          ,width: 150,
          height: 180,
          alignment: 'center',
        },
        {
          text: 'REPORTE DE CONSULTAS',style: 'header'
        },

        {
          text: 'desde ' +this.appaoutingaodule.cambiarfechageneral( this.desde) + "        hasta " + this.appaoutingaodule.cambiarfechageneral( this.hasta),
          style: 'subheader'
        },
        {
          style: 'tableExample',
          table: {
            headerRows: 1,
            body: this.data
          },
          layout: {
            fillColor: function (rowIndex, node, columnIndex) {
              return (rowIndex % 100000000 === 0) ? '#68C75A' : null;
            }
          }

        }
        

      ],
      styles: {
        header: {
          fontSize: 20,
          bold: true,
          margin: [0, 25, 0, 0],
          alignment: 'center',
        },
        subheader: {
          margin: [0, 4, 0, 0],
          alignment: 'center',
          fontSize: 11,
        },
        tableExample: {
          margin: [0, 25, 0, 15]
        },
        tableHeader: {
          bold: true,
          fontSize: 13,
          color: 'black'
        }
      },


      

    }

    this.pdfObj = pdfMake.createPdf(docDef);
    this.pdfObj.download( 'Reporte General de citas: '+this.today );
  }


}
